mapper.update:
/* 광고 승인버튼 클릭시 진행 상태가 결제전 으로 변경 */
UPDATE ad_apply_permit
SET
	  ad_permit_state = '결제전'
	, manager_id = #{managerId}
	, ad_permit_date = CURDATE()
WHERE ad_apply_code = #{adApplyCode};

/* 광고 승인취소 버튼 클릭시 진행 상태가 광고불가 로 변경 */
UPDATE ad_apply_permit
SET
	ad_permit_state= '광고불가'
	, manager_id = #{managerId}
	, ad_permit_date = CURDATE()
WHERE ad_apply_code= #{adApplyCode};

mapper.insert:
/* 광고 신청 등록 */
INSERT INTO ad_apply_permit
	(ad_apply_code
	, ad_price_code
	, member_id
	, ad_apply_title
	, ad_cate
	, ad_contract_price
	, ad_apply_date
	, ad_start_date
	, ad_end_date
	, ad_contract_term
	, ad_goods_url
	, ad_content
	, ad_img
	, ad_permit_state
	) VALUES (
			  #{adApplyCode}
			, #{adPriceCode}
			, #{memberId}
			, #{adApplyTitle}
			, #{adCate}
			, #{adContractPrice}
			, CURDATE()
			, #{adStartDate}
			, #{adEndDate}
			, #{adContractTerm}
			, #{adGoodsUrl}
			, #{adContent}
			, #{adImg}
			, '신청중' );

mapper.select:
/* 광고 신청 목록 조회 */ SELECT a.ad_apply_code , a.ad_price_code , a.member_id , a.ad_apply_title , a.ad_cate , a.ad_contract_price , a.ad_apply_date , a.ad_start_date , a.ad_end_date , a.ad_contract_term , a.ad_goods_url , a.ad_content , a.ad_img , a.ad_permit_state , a.manager_id , a.ad_permit_date FROM ad_apply_permit AS a
a.member_id = #{memberId}
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY cast(SUBSTRING_INDEX(a.ad_apply_code,'_',-1) AS UNSIGNED) ASC;

mapper.update:
/* 광고 신청 정보 수정 */
UPDATE ad_apply_permit AS a
SET
	a.ad_apply_title		=#{adApplyTitle}
	, a.ad_cate				=#{adCate}
	, a.ad_contract_price	=#{adContractPrice}
	, a.ad_start_date		=#{adStartDate}
	, a.ad_end_date			=#{adEndDate}
	, a.ad_contract_term	=#{adContractTerm}
	, a.ad_goods_url		=#{adGoodsUrl}
	, a.ad_content			=#{adContent}
	, a.ad_img				=#{adImg}
WHERE a.ad_apply_code		=#{adApplyCode}

mapper.select:
/* 광고 단가 목록 조회 */
SELECT a.ad_price_code ,a.ad_cate ,a.ad_price_per_day ,a.member_id ,a.entry_time FROM ad_price AS a
ORDER BY a.ad_price_code;

mapper.insert:
/* 광고 단가 등록 */	
INSERT INTO ad_price
	(ad_price_code
	, member_id
	, ad_cate
	, ad_price_per_day
	, entry_time
	) VALUES (
		#{adPriceCode}
		, #{memberId}
		, #{adCate}
		, #{adPricePerDay}
		, CURDATE());
