mapper.select:
/* 광고 단가 목록 조회 */
SELECT a.ad_price_code ,a.ad_cate ,a.ad_price_per_day ,a.member_id ,a.entry_time FROM ad_price AS a
ORDER BY a.ad_price_code;

mapper.insert:
INSERT INTO ad_price
	(ad_price_code
	, member_id
	, ad_cate
	, ad_price_per_day
	, entry_time
	) VALUES (
		#{adPriceCode}
		, #{memberId}
		, #{adCate}
		, #{adPricePerDay}
		, CURDATE());
