mapper.insert:
/* 상품 대분류 카테고리 등록 */
INSERT INTO goods_large
	(goods_large_cate
	, member_id
	, goods_large_name
	, entry_time
	)VALUES (
		#{goodsLargeCate}
		, #{memberId}
		, #{goodsLargeName}
		, CURDATE());

/* 상품 소분류 카테고리 등록 */
INSERT INTO goods_small
	(goods_small_cate
	, goods_large_cate
	, member_id
	, goods_small_name
	, entry_time
	)VALUES (
		#{goodsSmallCate}
		, #{goodsLargeCate}
		, #{memberId}
		, #{goodsSmallName}
		, CURDATE());

mapper.update:
/* 소분류 카테고리 수정 */ UPDATE goods_small
goods_large_cate = #{goodsLargeCate},
update_manager_id = #{updateManagerId},
goods_small_name = #{goodsSmallName},
last_update_time = CURDATE()
WHERE goods_small_cate = #{goodsSmallCate};

mapper.select:
/* 상품 대분류 카테고리 목록 조회 */
SELECT 
	l.goods_large_cate
	, l.goods_large_name
FROM 
	goods_large AS l;
