mapper.select:
/* 모든 조건별 회원 내역 조회 */ SELECT m.member_id ,m.member_name ,m.member_level ,m.member_nickname ,m.member_post_num ,m.member_addr ,m.member_detail_addr ,m.member_phone ,m.member_birth ,m.member_email ,m.member_date FROM member AS m
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
AND m.member_date BETWEEN #{startDate} AND #{endDate}
AND m.member_date = #{startDate}

mapper.update:
/* 판매자 사업장 정보 수정 */
UPDATE
	seller_store
SET
	 seller_store_num = #{sellerStoreNum}
	,seller_store_name = #{sellerStoreName}
	,seller_license_num = #{sellerLicenseNum}
	,seller_store_phone = #{sellerStorePhone}
	,seller_store_post_num = #{sellerStorePostNum}
	,seller_store_addr = #{sellerStoreAddr}
	,seller_store_detail_addr = #{sellerStoreDetailAddr}
	,seller_tax_type = #{sellerTaxType}
WHERE
	seller_store_num = #{sellerStoreNum}

/* 회원 정보 수정 */
UPDATE member
SET
	 member_id = #{memberId}
	,member_name = #{memberName}
	,member_level = #{memberLevel}
	,member_nickname = #{memberNickname}
	,member_post_num = #{memberPostNum}
	,member_addr = #{memberAddr}
	,member_detail_addr = #{memberDetailAddr}
	,member_phone = #{memberPhone}
	,member_email = #{memberEmail}
WHERE 
	member_id = #{memberId}

mapper.insert:
/* 관리자 회원 가입 */
INSERT INTO member
(
	 member_id
	,member_name
	,member_level
	,member_pw
	,member_nickname
	,member_post_num
	,member_addr
	,member_detail_addr
	,member_phone
	,member_birth
	,member_email
	,member_point
	,member_date
)VALUES (
     #{memberId}
   , #{memberName}
   , #{memberLevel}
   , #{memberPw}
   , #{memberNickname}
   , #{memberPostNum}
   , #{memberAddr}
   , #{memberDetailAddr}
   , #{memberPhone}
   , #{memberBirth}
   , #{memberEmail}
   , #{memberPoint}
   , CURDATE()
);
