mapper.select:
/* 사용자 화면에서 상품 목록 조회 */ SELECT m.goods_refurb_code , m.seller_store_num , m.goods_large_code , m.goods_small_code , m.member_id , m.goods_large_cate , m.goods_small_cate , m.goods_refurb_sell_state , m.goods_refurb_name , m.goods_normal_price , m.goods_refurb_price , m.goods_refurb_count , m.goods_refurb_harvest_date , m.goods_refurb_organic , m.file_idx , m.goods_post_summary , m.goods_post_explain , m.goods_delivery_charge , m.goods_weight , m.entry_time , s.goods_small_name , l.goods_large_name , f.file_idx , f.file_path FROM goods_management AS m INNER JOIN goods_small AS s ON m.goods_small_cate = s.goods_small_cate INNER JOIN goods_large AS l ON m.goods_large_cate = l.goods_large_cate LEFT JOIN tb_file_control AS fc ON m.goods_refurb_code = fc.reference_code LEFT JOIN tb_file AS f ON fc.file_idx = f.file_idx
m.goods_refurb_name LIKE CONCAT('%',#{searchValue}, '%')
l.goods_large_cate = #{searchCate}
ORDER BY m.entry_time DESC ,cast(SUBSTRING_INDEX(m.goods_refurb_code,'_',-1) AS UNSIGNED) DESC;
